mapper.select:
        select 
          from sys_dict_index


				and unioncode = #{unioncode, jdbcType=VARCHAR}


				and dictcode = #{dictcode, jdbcType=INTEGER}


				and dictname = #{dictname, jdbcType=VARCHAR}


				and dictgroup = #{dictgroup, jdbcType=VARCHAR}


				and editflag = #{editflag, jdbcType=INTEGER}

        select count(1) from sys_dict_index


				and unioncode = #{unioncode, jdbcType=VARCHAR}


				and dictcode = #{dictcode, jdbcType=INTEGER}


				and dictname = #{dictname, jdbcType=VARCHAR}


				and dictgroup = #{dictgroup, jdbcType=VARCHAR}


				and editflag = #{editflag, jdbcType=INTEGER}

select 
  from sys_dict_index
 where 1=1 and unioncode = #{0} and dictcode = #{1}

mapper.update:
update sys_dict_index set 
       dictname = #{dictname, jdbcType=VARCHAR}, 
       dictgroup = #{dictgroup, jdbcType=VARCHAR}, 
       editflag = #{editflag, jdbcType=INTEGER}
 where 1=1 and unioncode = #{unioncode, jdbcType=VARCHAR} and dictcode = #{dictcode, jdbcType=INTEGER}
