mapper.select:
        select 
          from sys_user_info


				and userid = #{userid, jdbcType=DECIMAL}


				and unioncode = #{unioncode, jdbcType=VARCHAR}


				and sex = #{sex, jdbcType=VARCHAR}


				and birthday = #{birthday, jdbcType=VARCHAR}


				and address = #{address, jdbcType=VARCHAR}


				and postcode = #{postcode, jdbcType=VARCHAR}


				and province = #{province, jdbcType=VARCHAR}


				and city = #{city, jdbcType=VARCHAR}


				and education = #{education, jdbcType=VARCHAR}

        select count(1) from sys_user_info


				and userid = #{userid, jdbcType=DECIMAL}


				and unioncode = #{unioncode, jdbcType=VARCHAR}


				and sex = #{sex, jdbcType=VARCHAR}


				and birthday = #{birthday, jdbcType=VARCHAR}


				and address = #{address, jdbcType=VARCHAR}


				and postcode = #{postcode, jdbcType=VARCHAR}


				and province = #{province, jdbcType=VARCHAR}


				and city = #{city, jdbcType=VARCHAR}


				and education = #{education, jdbcType=VARCHAR}

select 
  from sys_user_info
 where 1=1 and userid = #{0}
