mapper.select:
        select 
          from game_actor_skill


				and actorid = #{actorid, jdbcType=VARCHAR}


				and skillid = #{skillid, jdbcType=VARCHAR}


				and unlocklevel = #{unlocklevel, jdbcType=INTEGER}


				and extendflag = #{extendflag, jdbcType=INTEGER}


				and sno = #{sno, jdbcType=INTEGER}

select 
  from game_actor_skill
 where 1=1 and actorid = #{0} and skillid = #{1}

mapper.update:
update game_actor_skill set 
       unlocklevel = #{unlocklevel, jdbcType=INTEGER}, 
       extendflag = #{extendflag, jdbcType=INTEGER}, 
       sno = #{sno, jdbcType=INTEGER}
 where 1=1 and actorid = #{actorid, jdbcType=VARCHAR} and skillid = #{skillid, jdbcType=VARCHAR}
