mapper.select:
        select 
          from game_attribute_info


				and attrid = #{attrid, jdbcType=DECIMAL}


				and attrtype = #{attrtype, jdbcType=INTEGER}


				and attrfield = #{attrfield, jdbcType=INTEGER}


				and unlock = #{unlock, jdbcType=INTEGER}


				and act = #{act, jdbcType=INTEGER}


				and valtype = #{valtype, jdbcType=INTEGER}


				and valmin = #{valmin, jdbcType=DOUBLE}


				and valmax = #{valmax, jdbcType=DOUBLE}


				and initval = #{initval, jdbcType=DOUBLE}


				and upval = #{upval, jdbcType=DOUBLE}


				and position = #{position, jdbcType=VARCHAR}

select 
  from game_attribute_info
 where 1=1 and attrid = #{0}
