mapper.select:
        select 
          from game_equip_skill


				and equipid = #{equipid, jdbcType=VARCHAR}


				and skillid = #{skillid, jdbcType=VARCHAR}


				and proficiency = #{proficiency, jdbcType=INTEGER}


				and sno = #{sno, jdbcType=INTEGER}

select 
  from game_equip_skill
 where 1=1 and equipid = #{0} and skillid = #{1}

mapper.update:
update game_equip_skill set 
       proficiency = #{proficiency, jdbcType=INTEGER}, 
       sno = #{sno, jdbcType=INTEGER}
 where 1=1 and equipid = #{equipid, jdbcType=VARCHAR} and skillid = #{skillid, jdbcType=VARCHAR}
