mapper.select:
        select 
          from game_skill_info


				and skillid = #{skillid, jdbcType=VARCHAR}


				and skillname = #{skillname, jdbcType=VARCHAR}


				and icon = #{icon, jdbcType=VARCHAR}


				and skilltype = #{skilltype, jdbcType=INTEGER}


				and skillflag = #{skillflag, jdbcType=INTEGER}


				and skillfrom = #{skillfrom, jdbcType=INTEGER}


				and initlevel = #{initlevel, jdbcType=INTEGER}


				and maxlevel = #{maxlevel, jdbcType=INTEGER}


				and remark = #{remark, jdbcType=VARCHAR}


				and prop = #{prop, jdbcType=INTEGER}


				and target = #{target, jdbcType=INTEGER}


				and direct = #{direct, jdbcType=INTEGER}


				and mode = #{mode, jdbcType=INTEGER}


				and valtype = #{valtype, jdbcType=INTEGER}


				and initvalue = #{initvalue, jdbcType=DOUBLE}


				and upvalue = #{upvalue, jdbcType=DOUBLE}


				and princetype = #{princetype, jdbcType=INTEGER}


				and prince = #{prince, jdbcType=INTEGER}

select 
  from game_skill_info
 where 1=1 and skillid = #{0}
