mapper.select:
        select 
          from server_player_actor_skill


				and paid = #{paid, jdbcType=DECIMAL}


				and skillid = #{skillid, jdbcType=VARCHAR}


				and level = #{level, jdbcType=INTEGER}

select 
  from server_player_actor_skill
 where 1=1 and paid = #{0} and skillid = #{1}

mapper.update:
update server_player_actor_skill set 
       level = #{level, jdbcType=INTEGER}
 where 1=1 and paid = #{paid, jdbcType=DECIMAL} and skillid = #{skillid, jdbcType=VARCHAR}

