mapper.select:
        select 
          from server_player_equip


				and peid = #{peid, jdbcType=DECIMAL}


				and serverid = #{serverid, jdbcType=VARCHAR}


				and custno = #{custno, jdbcType=DECIMAL}


				and equipid = #{equipid, jdbcType=VARCHAR}


				and actorid = #{actorid, jdbcType=VARCHAR}


				and level = #{level, jdbcType=INTEGER}


				and proficiency = #{proficiency, jdbcType=INTEGER}

select 
  from server_player_equip
 where 1=1 and serverid = #{0} and custno = #{1} and equipid = #{2}

mapper.update:
update server_player_equip set 
       peid = #{peid, jdbcType=DECIMAL}, 
       actorid = #{actorid, jdbcType=VARCHAR}, 
       level = #{level, jdbcType=INTEGER}, 
       proficiency = #{proficiency, jdbcType=INTEGER}
 where 1=1 and serverid = #{serverid, jdbcType=VARCHAR} and custno = #{custno, jdbcType=DECIMAL} and equipid = #{equipid, jdbcType=VARCHAR}
