mapper.select:
        select 
          from sys_dict_value


				and unioncode = #{unioncode, jdbcType=VARCHAR}


				and dictcode = #{dictcode, jdbcType=INTEGER}


				and itemcode = #{itemcode, jdbcType=VARCHAR}


				and itemname = #{itemname, jdbcType=VARCHAR}

select 
  from sys_dict_value
 where 1=1 and unioncode = #{0} and dictcode = #{1} and itemcode = #{2}

mapper.update:
update sys_dict_value set 
       itemname = #{itemname, jdbcType=VARCHAR}
 where 1=1 and unioncode = #{unioncode, jdbcType=VARCHAR} and dictcode = #{dictcode, jdbcType=INTEGER} and itemcode = #{itemcode, jdbcType=VARCHAR}

update sys_dict_value


		itemname = #{itemname, jdbcType=VARCHAR},


where 1=1 and unioncode = #{unioncode, jdbcType=VARCHAR} and dictcode = #{dictcode, jdbcType=INTEGER} and itemcode = #{itemcode, jdbcType=VARCHAR}
