mapper.select:
        select 
          from sys_log_login


				and logid = #{logid, jdbcType=VARCHAR}


				and userid = #{userid, jdbcType=DECIMAL}


				and logindate = #{logindate, jdbcType=VARCHAR}


				and logintime = #{logintime, jdbcType=VARCHAR}


				and loginpasswd = #{loginpasswd, jdbcType=VARCHAR}


				and loginip = #{loginip, jdbcType=VARCHAR}


				and logintype = #{logintype, jdbcType=INTEGER}


				and loginresult = #{loginresult, jdbcType=INTEGER}

select 
  from sys_log_login
 where 1=1 and logid = #{0}
