mapper.select:
        select 
          from sys_param


				and unioncode = #{unioncode, jdbcType=VARCHAR}


				and paramid = #{paramid, jdbcType=INTEGER}


				and paramvalue = #{paramvalue, jdbcType=VARCHAR}

select 
  from sys_param
 where 1=1 and unioncode = #{0} and paramid = #{1}

mapper.update:
update sys_param set 
       paramvalue = #{paramvalue, jdbcType=VARCHAR}
 where 1=1 and unioncode = #{unioncode, jdbcType=VARCHAR} and paramid = #{paramid, jdbcType=INTEGER}

update sys_param


		paramvalue = #{paramvalue, jdbcType=VARCHAR},


where 1=1 and unioncode = #{unioncode, jdbcType=VARCHAR} and paramid = #{paramid, jdbcType=INTEGER}
